// Function to check if a digit is odd
bool isOdd(int digit) {
    return digit % 2 != 0;
}

// Function to get the first digit of a number
int getFirstDigit(int num) {
    while (num >= 10) {
        num /= 10;
    }
    return abs(num); // Ensure it's positive
}

// Function to get the last digit of a number
int getLastDigit(int num) {
    return abs(num) % 10; // Ensure it's positive
}

// Main function to filter the numbers
int specialFilter(vector<int> nums) {
    int count = 0;
    for (int num : nums) {
        if (num > 10 && isOdd(getFirstDigit(num)) && isOdd(getLastDigit(num))) {
            count++;
        }
    }
    return count;
}

// Function to test the specialFilter function
void testSpecialFilter() {
    vector<int> test1 = {15, -73, 14, -15};
    vector<int> test2 = {33, -2, -3, 45, 21, 109};

    cout << "Test 1: Expected 1, Got " << specialFilter(test1) << endl;
    cout << "Test 2: Expected 2, Got " << specialFilter(test2) << endl;
}


