using namespace std;

int get_max_triples(int n) {
    vector<int> a(n);
    vector<int> remainder_count(3, 0);

    // Generate the vector a and count the remainders
    for (int i = 1; i <= n; ++i) {
        a[i-1] = i * i - i + 1;
        int remainder = a[i-1] % 3;
        remainder_count[remainder]++;
    }

    // Count the number of valid triples
    long long count = 0;

    // Case 1: All three remainders are 0
    count += (long long)remainder_count[0] * (remainder_count[0] - 1) * (remainder_count[0] - 2) / 6;

    // Case 2: All three remainders are 1
    count += (long long)remainder_count[1] * (remainder_count[1] - 1) * (remainder_count[1] - 2) / 6;

    // Case 3: All three remainders are 2
    count += (long long)remainder_count[2] * (remainder_count[2] - 1) * (remainder_count[2] - 2) / 6;

    // Case 4: One remainder is 0, one is 1, and one is 2
    count += (long long)remainder_count[0] * remainder_count[1] * remainder_count[2];

    return count;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (get_max_triples(5) == 1);
    assert (get_max_triples(6) == 4);
    assert (get_max_triples(10) == 36);
    assert (get_max_triples(100) == 53361);
}

