// Function to calculate the sum of digits of a number
int sum_of_digits(int num) {
    int sum = 0;
    bool is_negative = num < 0;
    num = abs(num);

    while (num > 0) {
        sum += num % 10;
        num /= 10;
    }

    if (is_negative) {
        sum = -sum; // If the number was negative, make the sum negative
    }

    return sum;
}

// Function to sort the vector based on the sum of digits
vector<int> order_by_points(vector<int> nums) {
    // Create a vector of pairs (number, sum of digits, original index)
    vector<pair<int, pair<int, int>>> pairs;
    for (int i = 0; i < nums.size(); ++i) {
        pairs.push_back({nums[i], {sum_of_digits(nums[i]), i}});
    }

    // Sort the vector of pairs
    sort(pairs.begin(), pairs.end(), [](const pair<int, pair<int, int>>& a, const pair<int, pair<int, int>>& b) {
        if (a.second.first == b.second.first) {
            return a.second.second < b.second.second; // If sums are equal, compare by original index
        }
        return a.second.first < b.second.first; // Otherwise, compare by sum of digits
    });

    // Extract the sorted numbers
    vector<int> sorted_nums;
    for (const auto& p : pairs) {
        sorted_nums.push_back(p.first);
    }

    return sorted_nums;
}

