// Function to get the index of a planet in the solar system
int getPlanetIndex(const string& planet) {
    const vector<string> planets = {"Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"};

    for (int i = 0; i < planets.size(); ++i) {
        if (planets[i] == planet) {
            return i;
        }
    }
    return -1; // Return -1 if the planet name is invalid
}

// Function to find planets between two given planets
vector<string> bf(const string& planet1, const string& planet2) {
    const vector<string> planets = {"Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"};

    int index1 = getPlanetIndex(planet1);
    int index2 = getPlanetIndex(planet2);

    if (index1 == -1 || index2 == -1) {
        return {}; // Return an empty vector if either planet name is invalid
    }

    if (index1 > index2) {
        swap(index1, index2); // Ensure index1 is always less than or equal to index2
    }

    vector<string> result;
    for (int i = index1 + 1; i < index2; ++i) {
        result.push_back(planets[i]);
    }

    return result;
}

