vector<string> sorted_list_sum(vector<string> lst) {
    // Step 1: Remove strings with odd lengths
    lst.erase(remove_if(lst.begin(), lst.end(), [](const string& s) {
        return s.length() % 2 != 0;
    }), lst.end());

    // Step 2: Sort the remaining strings
    // First by length, then alphabetically
    sort(lst.begin(), lst.end(), [](const string& a, const string& b) {
        if (a.length() == b.length()) {
            return a < b; // Alphabetical order
        }
        return a.length() < b.length(); // Length order
    });

    return lst;
}

