// Function to calculate the strength of an extension
int calculate_strength(const string& extension) {
    int cap = 0, sm = 0;
    for (char c : extension) {
        if (isupper(c)) cap++;
        else if (islower(c)) sm++;
    }
    return cap - sm;
}

string Strongest_Extension(string class_name, vector<string> extensions) {
    // Initialize the strongest extension and its strength
    string strongest_extension = "";
    int max_strength = INT_MIN;

    // Iterate through each extension to find the strongest one
    for (const string& extension : extensions) {
        int strength = calculate_strength(extension);
        if (strength > max_strength) {
            max_strength = strength;
            strongest_extension = extension;
        }
    }

    // Return the class name and the strongest extension in the required format
    return class_name + "." + strongest_extension;
}

// Function to test the solution
void test() {
    string class_name = "Slices";
    vector<string> extensions = {"SErviNGSliCes", "Cheese", "StuFfed"};
    string result = Strongest_Extension(class_name, extensions);
    cout << "Result: " << result << endl; // Expected: "Slices.SErviNGSliCes"

    class_name = "my_class";
    extensions = {"AA", "Be", "CC"};
    result = Strongest_Extension(class_name, extensions);
    cout << "Result: " << result << endl; // Expected: "my_class.AA"
}


