    vector<int> result1 = even_odd_count(-12);
    cout << "even_odd_count(-12) ==> {" << result1[0] << ", " << result1[1] << "}" << endl;

    vector<int> result2 = even_odd_count(123);
    cout << "even_odd_count(123) ==> {" << result2[0] << ", " << result2[1] << "}" << endl;
}


