// Function to check if any rotation of b is a substring of a
bool cycpattern_check(const string& a, const string& b) {
    // Concatenate a with itself to handle rotations
    string doubled_a = a + a;

    // Check all possible rotations of b
    for (size_t i = 0; i < b.length(); ++i) {
        string rotated_b = b.substr(i) + b.substr(0, i);
        if (doubled_a.find(rotated_b) != string::npos) {
            return true;
        }
    }
    return false;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert  (cycpattern_check("xyzw","xyw") == false );
    assert  (cycpattern_check("yello","ell") == true );
    assert  (cycpattern_check("whattup","ptut") == false );
    assert  (cycpattern_check("efef","fee") == true );
    assert  (cycpattern_check("abab","aabb") == false );
    assert  (cycpattern_check("winemtt","tinem") == true );
}

