string int_to_mini_roman(int number) {
    // Define the Roman numeral mappings
    vector<pair<int, string>> roman_map = {
        {1000, "m"}, {900, "cm"}, {500, "d"}, {400, "cd"},
        {100, "c"}, {90, "xc"}, {50, "l"}, {40, "xl"},
        {10, "x"}, {9, "ix"}, {5, "v"}, {4, "iv"}, {1, "i"}
    };

    string result = "";

    // Iterate through the roman_map to construct the Roman numeral
    for (const auto &p : roman_map) {
        while (number >= p.first) {
            result += p.second;
            number -= p.first;
        }
    }

    return result;
}

// Function to test the correctness of the solution
void test() {
    cout << (int_to_mini_roman(19) == "xix") << endl;
    cout << (int_to_mini_roman(152) == "clii") << endl;
    cout << (int_to_mini_roman(426) == "cdxxvi") << endl;
}


