using namespace std;

string find_max(vector<string> words) {
    string max_word = "";
    int max_unique_chars = 0;

    for (const string& word : words) {
        set<char> unique_chars(word.begin(), word.end());
        int num_unique_chars = unique_chars.size();

        if (num_unique_chars > max_unique_chars || 
            (num_unique_chars == max_unique_chars && word < max_word)) {
            max_word = word;
            max_unique_chars = num_unique_chars;
        }
    }

    return max_word;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert ((find_max({"name", "of", "string"}) == "string"));
    assert ((find_max({"name", "enam", "game"}) == "enam"));
    assert ((find_max({"aaaaaaa", "bb", "cc"}) == "aaaaaaa"));
    assert ((find_max({"abc", "cba"}) == "abc"));
    assert ((find_max({"play", "this", "game", "of","footbott"}) == "footbott"));
    assert ((find_max({"we", "are", "gonna", "rock"}) == "gonna"));
    assert ((find_max({"we", "are", "a", "mad", "nation"}) == "nation"));
    assert ((find_max({"this", "is", "a", "prrk"}) == "this"));
    assert ((find_max({"b"}) == "b"));
    assert ((find_max({"play", "play", "play"}) == "play"));
}

