#include <cctype> // for isalpha and tolower/toupper functions

using namespace std;

string solve(string s) {
    bool hasLetter = false;

    // Check if the string contains any letters
    for (char c : s) {
        if (isalpha(c)) {
            hasLetter = true;
            break;
        }
    }

    // If the string contains no letters, reverse the string
    if (!hasLetter) {
        reverse(s.begin(), s.end());
        return s;
    }

    // If the string contains letters, reverse their case
    for (char &c : s) {
        if (isalpha(c)) {
            if (islower(c)) {
                c = toupper(c);
            } else {
                c = tolower(c);
            }
        }
    }

    return s;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (solve("AsDf") == "aSdF");
    assert (solve("1234") == "4321");
    assert (solve("ab") == "AB");
    assert (solve("#a@C") == "#A@c");
    assert (solve("#AsdfW^45") == "#aSDFw^45");
    assert (solve("#6@2") == "2@6#");
    assert (solve("#$a^D") == "#$A^d");
    assert (solve("#ccc") == "#CCC");
}

