using namespace std;

int do_algebra(vector<string> operato, vector<int> operand) {
    int result = operand[0]; // Initialize result with the first operand

    for (size_t i = 0; i < operato.size(); ++i) {
        if (operato[i] == "+") {
            result += operand[i + 1];
        } else if (operato[i] == "-") {
            result -= operand[i + 1];
        } else if (operato[i] == "*") {
            result *= operand[i + 1];
        } else if (operato[i] == "//") {
            result /= operand[i + 1]; // Floor division in C++ is the default integer division
        } else if (operato[i] == "**") {
            result = pow(result, operand[i + 1]);
        }
    }

    return result;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (do_algebra({"**", "*", "+"}, {2, 3, 4, 5}) == 37);
    assert (do_algebra({"+", "*", "-"}, {2, 3, 4, 5}) == 9);
    assert (do_algebra({"//", "*"}, {7, 3, 4}) == 8);
}

