void test_generate_integers() {
    vector<int> result1 = generate_integers(2, 8);
    vector<int> expected1 = {2, 4, 6, 8};
    cout << "Test 1: " << (result1 == expected1 ? "Passed" : "Failed") << endl;

    vector<int> result2 = generate_integers(8, 2);
    vector<int> expected2 = {2, 4, 6, 8};
    cout << "Test 2: " << (result2 == expected2 ? "Passed" : "Failed") << endl;

    vector<int> result3 = generate_integers(10, 14);
    vector<int> expected3 = {};
    cout << "Test 3: " << (result3 == expected3 ? "Passed" : "Failed") << endl;
}


