import java.util.*;

class Solution {
    /**
    Check if in given list of numbers, are any two numbers closer to each other than given threshold.
    */
    public boolean has_close_elements(List<Double> numbers, double threshold) {
        // Sort the list to ensure that we only need to check adjacent elements
        Collections.sort(numbers);

        // Iterate through the sorted list and check the difference between adjacent elements
        for (int i = 0; i < numbers.size() - 1; i++) {
            double diff = Math.abs(numbers.get(i) - numbers.get(i + 1));
            if (diff < threshold) {
                return true;
            }
        }

        // If no two elements are closer than the threshold, return false
        return false;
    }

    public static void main(String[] args) {
