}

import java.util.*;

class Solution {
    public List<String> separate_paren_groups(String paren_string) {
        List<String> result = new ArrayList<>();
        Stack<Character> stack = new Stack<>();
        StringBuilder currentGroup = new StringBuilder();

        for (char c : paren_string.toCharArray()) {
            if (c == ' ') continue; // Ignore spaces

            if (c == '(') {
                stack.push(c);
                currentGroup.append(c);
            } else if (c == ')' && !stack.isEmpty()) {
                stack.pop();
                currentGroup.append(c);

                if (stack.isEmpty()) {
                    // A complete group is found
                    result.add(currentGroup.toString());
                    currentGroup.setLength(0); // Reset the current group
                }
            }
        }

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        String input = "( ) (( )) (( )( ))";
        List<String> output = solution.separate_paren_groups(input);
        System.out.println(output); // Expected: [(), (()), (()())]
    }
}

