class Solution {
    public boolean below_zero(List<Integer> operations) {
        int balance = 0; // Start with a zero balance

        for (int operation : operations) {
            balance += operation; // Update the balance with each operation

            if (balance < 0) {
                return true; // Return true if balance falls below zero
            }
        }

        return false; // Return false if balance never falls below zero
    }

