    }
}

import java.util.*;

class Solution {
    /**
    For a given list of input numbers, calculate Mean Absolute Deviation
    around the mean of this dataset.
    Mean Absolute Deviation is the average absolute difference between each
    element and a centerpoint (mean in this case):
    MAD = average | x - x_mean |
    >>> mean_absolute_deviation(Arrays.asList(1.0, 2.0, 3.0, 4.0))
    1.0
     */
    public double mean_absolute_deviation(List<Double> numbers) {
        if (numbers == null || numbers.isEmpty()) {
            throw new IllegalArgumentException("List of numbers cannot be null or empty");
        }

        // Step 1: Calculate the mean of the numbers
        double sum = 0.0;
        for (double number : numbers) {
            sum += number;
        }
        double mean = sum / numbers.size();

        // Step 2: Compute the absolute differences from the mean
        double totalAbsoluteDifference = 0.0;
        for (double number : numbers) {
            totalAbsoluteDifference += Math.abs(number - mean);
        }

        // Step 3: Calculate the average of these absolute differences
        double mad = totalAbsoluteDifference / numbers.size();

        return mad;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        List<Double> numbers = Arrays.asList(1.0, 2.0, 3.0, 4.0);
        double mad = solution.mean_absolute_deviation(numbers);
        System.out.println("Mean Absolute Deviation: " + mad); // Expected output: 1.0
    }
}

