class Solution {
    /**
    Insert a number 'delimiter' between every two consecutive elements of input list `numbers'
    >>> intersperse(List.of(), 4)
    []
    >>> intersperse(Arrays.asList(1, 2, 3), 4)
    [1, 4, 2, 4, 3]
     */
    public List<Integer> intersperse(List<Integer> numbers, int delimiter) {
        // If the input list is empty or has only one element, return it as is.
        if (numbers == null || numbers.size() <= 1) {
            return numbers;
        }

        // Create a new list to store the result.
        List<Integer> result = new ArrayList<>();

        // Iterate through the input list, except the last element.
        for (int i = 0; i < numbers.size() - 1; i++) {
            // Add the current element to the result list.
            result.add(numbers.get(i));
            // Add the delimiter after the current element.
            result.add(delimiter);
        }

        // Add the last element of the input list to the result list.
        result.add(numbers.get(numbers.size() - 1));

        return result;
    }

    // Main method for testing the intersperse function.
    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.intersperse(List.of(), 4)); // []
        System.out.println(solution.intersperse(Arrays.asList(1, 2, 3), 4)); // [1, 4, 2, 4, 3]
    }
}

