class Solution {
    /**
     * Test if given string is a palindrome
     */
    public boolean isPalindrome(String string) {
        int i = 0;
        int j = string.length() - 1;
        while (i < j) {
            if (string.charAt(i) != string.charAt(j)) {
                return false;
            }
            i++;
            j--;
        }
        return true;
    }

    /**
     * Find the shortest palindrome that begins with a supplied string.
     * Algorithm idea is simple:
     * - Find the longest postfix of supplied string that is a palindrome.
     * - Append to the end of the string reverse of a string prefix that comes before the palindromic suffix.
     */
    public String make_palindrome(String string) {
        if (string == null || string.isEmpty()) {
            return string;
        }

        // Find the longest palindromic suffix
        int len = string.length();
        int i = len;
        while (i > 0 && !isPalindrome(string.substring(0, i))) {
            i--;
        }

        // The part of the string that is not a palindrome
        String nonPalindromePrefix = string.substring(i);

        // Reverse the non-palindromic prefix
        String reversedNonPalindromePrefix = new StringBuilder(nonPalindromePrefix).reverse().toString();

        // Append the reversed non-palindromic prefix to the original string
        return string + reversedNonPalindromePrefix;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.make_palindrome(""));      // Output: ""
        System.out.println(solution.make_palindrome("cat"));   // Output: "catac"
        System.out.println(solution.make_palindrome("cata"));  // Output: "catac"
    }
}

