class Solution {
    /**
    Input are two strings a and b consisting only of 1s and 0s.
    Perform binary XOR on these inputs and return result also as a string.
    >>> string_xor("010", "110")
    "100"
     */
    public String string_xor(String a, String b) {
        // Ensure both strings are of the same length
        int maxLength = Math.max(a.length(), b.length());
        a = String.format("%" + maxLength + "s", a).replace(' ', '0');
        b = String.format("%" + maxLength + "s", b).replace(' ', '0');

        StringBuilder result = new StringBuilder();

        // Perform XOR operation on each character
        for (int i = 0; i < maxLength; i++) {
            char charA = a.charAt(i);
            char charB = b.charAt(i);
            if (charA == charB) {
                result.append('0');
            } else {
                result.append('1');
            }
        }

        return result.toString();
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.string_xor("010", "110")); // Output: "100"
        System.out.println(solution.string_xor("10101", "00111")); // Output: "10010"
    }
}

