class Solution {
    public Optional<String> longest(List<String> strings) {
        if (strings.isEmpty()) {
            return Optional.empty();
        }

        String longestString = strings.get(0);
        for (String str : strings) {
            if (str.length() > longestString.length()) {
                longestString = str;
            }
        }

        return Optional.of(longestString);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.longest(List.of())); // Optional.empty
        System.out.println(solution.longest(Arrays.asList("a", "b", "c"))); // Optional[a]
        System.out.println(solution.longest(Arrays.asList("a", "bb", "ccc"))); // Optional[ccc]
    }
}

