class Solution {
    /**
     * Returns the greatest common divisor of two integers a and b.
     * 
     * @param a the first integer
     * @param b the second integer
     * @return the greatest common divisor of a and b
     */
    public int greatest_common_divisor(int a, int b) {
        // Ensure that both a and b are positive
        a = Math.abs(a);
        b = Math.abs(b);

        // Base case: if b is 0, gcd is a
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }

        return a;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.greatest_common_divisor(3, 5));  // Output: 1
        System.out.println(solution.greatest_common_divisor(25, 15));  // Output: 5
        System.out.println(solution.greatest_common_divisor(-36, 24));  // Output: 12
    }
}

