class Solution {
    /**
    Return a string containing space-delimited numbers starting from 0 up to n inclusive.
    >>> string_sequence(0)
    "0"
    >>> string_sequence(5)
    "0 1 2 3 4 5"
     */
    public String string_sequence(int n) {
        if (n < 0) {
            return "";
        }

        StringBuilder sb = new StringBuilder();
        for (int i = 0; i <= n; i++) {
            sb.append(i);
            if (i < n) {
                sb.append(" ");
            }
        }
        return sb.toString();
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.string_sequence(0)); // Output: "0"
        System.out.println(sol.string_sequence(5)); // Output: "0 1 2 3 4 5"
    }
}

