class Solution {
    /**
    Given a string, find out how many distinct characters (regardless of case) does it consist of
    >>> count_distinct_characters("xyzXYZ")
    3
    >>> count_distinct_characters("Jerry")
    4
     */
    public int count_distinct_characters(String string) {
        // Create a HashSet to store unique characters
        Set<Character> uniqueChars = new HashSet<>();

        // Convert the string to lowercase to make the comparison case-insensitive
        String lowerCaseString = string.toLowerCase();

        // Iterate over each character in the string
        for (char c : lowerCaseString.toCharArray()) {
            // Add the character to the HashSet
            uniqueChars.add(c);
        }

        // The size of the HashSet represents the number of distinct characters
        return uniqueChars.size();
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.count_distinct_characters("xyzXYZ")); // Output: 3
        System.out.println(solution.count_distinct_characters("Jerry"));  // Output: 4
    }
}

import java.util.*;

class Solution {
    public List<Integer> parse_music(String string) {
        List<Integer> beats = new ArrayList<>();
        int i = 0;

        while (i < string.length()) {
            if (string.charAt(i) == 'o') {
                if (i + 1 < string.length() && string.charAt(i + 1) == '|') {
                    // Half note: "o|"
                    beats.add(2);
                    i += 2; // Move past "o|"
                } else {
                    // Whole note: "o"
                    beats.add(4);
                    i++; // Move past "o"
                }
            } else if (string.charAt(i) == '.') {
                // Quarter note: ".|"
                beats.add(1);
                i += 2; // Move past ".|"
            } else {
                // Skip spaces or any other characters
                i++;
            }
        }

        return beats;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        String input = "o o| .| o| o| .| .| .| .| o o";
        List<Integer> result = solution.parse_music(input);
        System.out.println(result); // Expected output: [4, 2, 1, 2, 2, 1, 1, 1, 1, 4, 4]
    }
}

