class Solution {
    /**
    Find how many times a given substring can be found in the original string. Count overlapping cases.
    >>> how_many_times("", "a")
    0
    >>> how_many_times("aaa", "a")
    3
    >>> how_many_times("aaaa", "aa")
    3
     */
    public int how_many_times(String string, String substring) {
        if (substring.isEmpty()) return 0;

        int count = 0;
        int index = 0;

        while (index != -1) {
            index = string.indexOf(substring, index);
            if (index != -1) {
                count++;
                index++; // Move to the next character to allow for overlapping matches
            }
        }

        return count;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.how_many_times("", "a")); // 0
        System.out.println(solution.how_many_times("aaa", "a")); // 3
        System.out.println(solution.how_many_times("aaaa", "aa")); // 3
    }
}

import java.util.*;
