class Solution {
    /**
    For a given number n, find the largest number that divides n evenly, smaller than n
    >>> largest_divisor(15)
    5
     */
    public int largest_divisor(int n) {
        // Start from n-1 and go down to 1
        for (int i = n - 1; i > 0; i--) {
            // Check if i divides n evenly
            if (n % i == 0) {
                return i; // Return the first (largest) divisor found
            }
        }
        // If no divisor is found, return 1 (every number is divisible by 1)
        return 1;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.largest_divisor(15)); // Output: 5
        System.out.println(sol.largest_divisor(28)); // Output: 14
        System.out.println(sol.largest_divisor(17)); // Output: 1 (17 is a prime number)
    }
}

import java.util.ArrayList;
import java.util.List;

class Solution {
    /**
    Return list of prime factors of given integer in the order from smallest to largest.
    Each of the factors should be listed number of times corresponding to how many times it appeares in factorization.
    Input number should be equal to the product of all factors
    >>> factorize(8)
    [2, 2, 2]
    >>> factorize(25)
    [5, 5]
    >>> factorize(70)
    [2, 5, 7]
     */
    public List<Integer> factorize(int n) {
        List<Integer> factors = new ArrayList<>();

        // Handle the case where n is less than 2
        if (n < 2) {
            return factors;
        }

        // Check for the number of 2s that divide n
        while (n % 2 == 0) {
            factors.add(2);
            n /= 2;
        }

        // n must be odd at this point, so we can skip one element (Note i = i + 2)
        for (int i = 3; i <= Math.sqrt(n); i += 2) {
            // While i divides n, add i and divide n
            while (n % i == 0) {
                factors.add(i);
                n /= i;
            }
        }

        // This condition is to handle the case when n is a prime number greater than 2
        if (n > 2) {
            factors.add(n);
        }

        return factors;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.factorize(8));  // Output: [2, 2, 2]
        System.out.println(solution.factorize(25)); // Output: [5, 5]
        System.out.println(solution.factorize(70)); // Output: [2, 5, 7]
    }
}

import java.util.*;
