import java.util.stream.Collectors;

class Solution {
    /**
    From a list of integers, remove all elements that occur more than once.
    Keep order of elements left the same as in the input.
    >>> remove_duplicates(Arrays.asList(1, 2, 3, 2, 4))
    [1, 3, 4]
     */
    public List<Integer> remove_duplicates(List<Integer> numbers) {
        // Count the occurrences of each element
        Map<Integer, Integer> countMap = new HashMap<>();
        for (int num : numbers) {
            countMap.put(num, countMap.getOrDefault(num, 0) + 1);
        }

        // Filter out elements that occur more than once and maintain the order
        List<Integer> result = new LinkedList<>();
        for (int num : numbers) {
            if (countMap.get(num) == 1) {
                result.add(num);
            }
        }

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        List<Integer> testList = Arrays.asList(1, 2, 3, 2, 4);
        List<Integer> result = solution.remove_duplicates(testList);
        System.out.println(result); // Expected output: [1, 3, 4]
    }
}

