class Solution {
    /**
    For a given string, flip lowercase characters to uppercase and uppercase to lowercase.
    >>> flip_case("Hello")
    "hELLO"
     */
    public String flip_case(String string) {
        StringBuilder result = new StringBuilder();

        for (char c : string.toCharArray()) {
            if (Character.isUpperCase(c)) {
                result.append(Character.toLowerCase(c));
            } else if (Character.isLowerCase(c)) {
                result.append(Character.toUpperCase(c));
            } else {
                result.append(c);
            }
        }

        return result.toString();
    }

    // A simple check function to verify the correctness of the solution
    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.flip_case("Hello")); // Expected output: "hELLO"
        System.out.println(sol.flip_case("Java123")); // Expected output: "jAVA123"
        System.out.println(sol.flip_case("12345")); // Expected output: "12345"
        System.out.println(sol.flip_case("AlibabaCloud")); // Expected output: "aLIBABAcLOUD"
    }
}

