import java.util.*;
import java.lang.*;

class Solution {
    /**
    Return true if a given number is prime, and false otherwise.
    */
    public boolean is_prime(int n) {
        // Handle edge cases for numbers less than 2
        if (n <= 1) {
            return false;
        }

        // 2 is the only even prime number
        if (n == 2) {
            return true;
        }

        // Exclude even numbers greater than 2
        if (n % 2 == 0) {
            return false;
        }

        // Check for factors from 3 to the square root of n
        for (int i = 3; i * i <= n; i += 2) {
            if (n % i == 0) {
                return false;
            }
        }

        return true;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.is_prime(6));    // false
        System.out.println(solution.is_prime(101));  // true
        System.out.println(solution.is_prime(11));   // true
        System.out.println(solution.is_prime(13441));// true
        System.out.println(solution.is_prime(61));   // true
        System.out.println(solution.is_prime(4));    // false
        System.out.println(solution.is_prime(1));    // false
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    /**
    Evaluates polynomial with coefficients xs at point x.
    return xs[0] + xs[1] * x + xs[1] * x^2 + .... xs[n] * x^n
     */
    public double poly(List<Double> xs, double x) {
        double result = 0;
        for (int i = 0; i < xs.size(); i++) {
            result += xs.get(i) * Math.pow(x, i);
        }
        return result;
    }

    /**
    Evaluates the derivative of the polynomial with coefficients xs at point x.
    return xs[1] + 2 * xs[2] * x + 3 * xs[3] * x^2 + .... n * xs[n] * x^(n-1)
     */
    public double polyDerivative(List<Double> xs, double x) {
        double result = 0;
        for (int i = 1; i < xs.size(); i++) {
            result += i * xs.get(i) * Math.pow(x, i - 1);
        }
        return result;
    }

    /**
    Finds a zero of the polynomial with coefficients xs using the Newton-Raphson method.
    This method assumes that the list xs has an even number of coefficients and the largest non-zero coefficient guarantees a solution.
     */
    public double find_zero(List<Double> xs) {
        if (xs.size() % 2 != 0) {
            throw new IllegalArgumentException("The list of coefficients must have an even number of elements.");
        }

        double x = 0; // Initial guess
        double epsilon = 1e-10; // Tolerance for convergence
        int maxIterations = 1000; // Maximum number of iterations

        for (int i = 0; i < maxIterations; i++) {
            double fx = poly(xs, x);
            double dfx = polyDerivative(xs, x);

            if (Math.abs(dfx) < epsilon) {
                throw new ArithmeticException("Derivative is too small; no convergence possible.");
            }

            double nextX = x - fx / dfx;

            if (Math.abs(nextX - x) < epsilon) {
                return nextX;
            }

            x = nextX;
        }

        throw new RuntimeException("Failed to converge to a solution within the maximum number of iterations.");
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        List<Double> coefficients1 = Arrays.asList(1.0, 2.0);
        List<Double> coefficients2 = Arrays.asList(-6.0, 11.0, -6.0, 1.0);

        System.out.println(sol.find_zero(coefficients1)); // Expected: -0.5
        System.out.println(sol.find_zero(coefficients2)); // Expected: 1.0
    }
}

