class Solution {
    public List<Integer> sort_third(List<Integer> l) {
        // Step 1: Collect elements at indices divisible by 3
        List<Integer> elementsToSort = new ArrayList<>();
        for (int i = 0; i < l.size(); i++) {
            if ((i + 1) % 3 == 0) {
                elementsToSort.add(l.get(i));
            }
        }

        // Step 2: Sort the collected elements
        Collections.sort(elementsToSort);

        // Step 3: Replace the original elements with the sorted ones
        int sortIndex = 0;
        for (int i = 0; i < l.size(); i++) {
            if ((i + 1) % 3 == 0) {
                l.set(i, elementsToSort.get(sortIndex++));
            }
        }

        return l;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.sort_third(Arrays.asList(1, 2, 3))); // [1, 2, 3]
        System.out.println(solution.sort_third(Arrays.asList(5, 6, 3, 4, 8, 9, 2))); // [2, 6, 3, 4, 8, 9, 5]
    }
}

