class Solution {
    /**
    This function takes a list l and returns a list l' such that
    l' is identical to l in the odd indicies, while its values at the even indicies are equal
    to the values of the even indicies of l, but sorted.
    >>> sort_even(Arrays.asList(1, 2, 3))
    [1, 2, 3]
    >>> sort_even(Arrays.asList(5, 6, 3, 4))
    [3, 6, 5, 4]
     */
    public List<Integer> sort_even(List<Integer> l) {
        // Step 1: Extract elements at even indices
        List<Integer> evenIndices = new ArrayList<>();
        for (int i = 0; i < l.size(); i += 2) {
            evenIndices.add(l.get(i));
        }

        // Step 2: Sort the extracted elements
        Collections.sort(evenIndices);

        // Step 3: Reconstruct the list
        List<Integer> result = new ArrayList<>(l);
        for (int i = 0, j = 0; i < result.size(); i += 2, j++) {
            result.set(i, evenIndices.get(j));
        }

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.sort_even(Arrays.asList(1, 2, 3))); // [1, 2, 3]
        System.out.println(solution.sort_even(Arrays.asList(5, 6, 3, 4))); // [3, 6, 5, 4]
    }
}

import java.util.ArrayList;
import java.util.List;

public class Decoder {
    public String decode_cyclic(String s) {
        List<String> groups = new ArrayList<>();
        for (int i = 0; i < s.length(); i += 3) {
            groups.add(s.substring(i, Math.min(i + 3, s.length())));
        }

        for (int i = 0; i < groups.size(); i++) {
            if (groups.get(i).length() == 3) {
                char thirdChar = groups.get(i).charAt(2);
                String firstTwoChars = groups.get(i).substring(0, 2);
                groups.set(i, thirdChar + "" + firstTwoChars);
            }
        }

        return String.join("", groups);
    }

    public static void main(String[] args) {
        Decoder decoder = new Decoder();
        String encodedString = "tsehllloW";
        String decodedString = decoder.decode_cyclic(encodedString);
        System.out.println("Decoded String: " + decodedString);
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    /**
    prime_fib returns n-th number that is a Fibonacci number and it's also prime.
    >>> prime_fib(1)
    2
    >>> prime_fib(2)
    3
    >>> prime_fib(3)
    5
    >>> prime_fib(4)
    13
    >>> prime_fib(5)
    89
     */
    public int prime_fib(int n) {
        int count = 0;
        int fib1 = 0;
        int fib2 = 1;

        while (true) {
            int fib = fib1 + fib2;
            fib1 = fib2;
            fib2 = fib;

            if (isPrime(fib)) {
                count++;
                if (count == n) {
                    return fib;
                }
            }
        }
    }

    private boolean isPrime(int num) {
        if (num <= 1) return false;
        if (num <= 3) return true;
        if (num % 2 == 0 || num % 3 == 0) return false;

        for (int i = 5; i * i <= num; i += 6) {
            if (num % i == 0 || num % (i + 2) == 0) return false;
        }

        return true;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.prime_fib(1)); // 2
        System.out.println(solution.prime_fib(2)); // 3
        System.out.println(solution.prime_fib(3)); // 5
        System.out.println(solution.prime_fib(4)); // 13
        System.out.println(solution.prime_fib(5)); // 89
    }
}

import java.util.*;

class Solution {
    public boolean triples_sum_to_zero(List<Integer> l) {
        // Sort the list to use the two-pointer technique
        Collections.sort(l);

        // Iterate through the list
        for (int i = 0; i < l.size() - 2; i++) {
            // Skip duplicate elements
            if (i > 0 && l.get(i).equals(l.get(i - 1))) {
                continue;
            }

            int left = i + 1;
            int right = l.size() - 1;

            while (left < right) {
                int sum = l.get(i) + l.get(left) + l.get(right);

                if (sum == 0) {
                    return true;
                } else if (sum < 0) {
                    left++;
                } else {
                    right--;
                }
            }
        }

        return false;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.triples_sum_to_zero(Arrays.asList(1, 3, 5, 0))); // false
        System.out.println(solution.triples_sum_to_zero(Arrays.asList(1, 3, -2, 1))); // true
        System.out.println(solution.triples_sum_to_zero(Arrays.asList(1, 2, 3, 7))); // false
        System.out.println(solution.triples_sum_to_zero(Arrays.asList(2, 4, -5, 3, 9, 7))); // true
        System.out.println(solution.triples_sum_to_zero(Arrays.asList(1))); // false
    }
}

