class Solution {
    public boolean pairs_sum_to_zero(List<Integer> l) {
        // Create a HashSet to store the numbers we have seen
        Set<Integer> seen = new HashSet<>();

        // Iterate through the list of numbers
        for (int num : l) {
            // Check if the negation of the current number is in the set
            if (seen.contains(-num)) {
                return true; // Found a pair that sums to zero
            }
            // Add the current number to the set
            seen.add(num);
        }

        // If no such pair is found, return false
        return false;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.pairs_sum_to_zero(Arrays.asList(1, 3, 5, 0))); // false
        System.out.println(sol.pairs_sum_to_zero(Arrays.asList(1, 3, -2, 1))); // false
        System.out.println(sol.pairs_sum_to_zero(Arrays.asList(1, 2, 3, 7))); // false
        System.out.println(sol.pairs_sum_to_zero(Arrays.asList(2, 4, -5, 3, 5, 7))); // true
        System.out.println(sol.pairs_sum_to_zero(Arrays.asList(1))); // false
    }
}

