class Solution {
    /**
    Change numerical base of input number x to base.
    return string representation after the conversion.
    base numbers are less than 10.
    >>> change_base(8, 3)
    "22"
    >>> change_base(8, 2)
    "1000"
    >>> change_base(7, 2)
    "111"
     */
    public String change_base(int x, int base) {
        if (base < 2 || base > 9) {
            throw new IllegalArgumentException("Base must be between 2 and 9");
        }

        StringBuilder result = new StringBuilder();
        while (x > 0) {
            int remainder = x % base;
            result.insert(0, remainder);
            x /= base;
        }

        return result.toString();
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.change_base(8, 3)); // Output: "22"
        System.out.println(solution.change_base(8, 2)); // Output: "1000"
        System.out.println(solution.change_base(7, 2)); // Output: "111"
    }
}

