import java.util.*;

class Solution {
    public int fib4(int n) {
        // Base cases
        if (n == 0 || n == 1 || n == 3) return 0;
        if (n == 2) return 2;

        // Initialize the first four values of the sequence
        int[] fib = new int[4];
        fib[0] = 0;
        fib[1] = 0;
        fib[2] = 2;
        fib[3] = 0;

        // Compute the n-th value iteratively
        for (int i = 4; i <= n; i++) {
            int nextValue = fib[0] + fib[1] + fib[2] + fib[3];
            // Shift the window to the right
            fib[0] = fib[1];
            fib[1] = fib[2];
            fib[2] = fib[3];
            fib[3] = nextValue;
        }

        // The last value in the array is the n-th value of the sequence
        return fib[3];
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.fib4(5)); // Output: 4
        System.out.println(sol.fib4(6)); // Output: 8
        System.out.println(sol.fib4(7)); // Output: 14
    }
}

import java.util.*;

class Solution {
    /**
     * Return median of elements in the list l.
     * >>> median(Arrays.asList(3, 1, 2, 4, 5))
     * 3
     * >>> median(Arrays.asList(-10, 4, 6, 1000, 10, 20))
     * 8.0
     */
    public double median(List<Integer> l) {
        // Sort the list
        Collections.sort(l);

        // Get the size of the list
        int n = l.size();

        // Check if the list has an odd or even number of elements
        if (n % 2 == 1) {
            // If odd, return the middle element
            return l.get(n / 2);
        } else {
            // If even, return the average of the two middle elements
            return (l.get(n / 2 - 1) + l.get(n / 2)) / 2.0;
        }
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.median(Arrays.asList(3, 1, 2, 4, 5))); // Output: 3.0
        System.out.println(solution.median(Arrays.asList(-10, 4, 6, 1000, 10, 20))); // Output: 8.0
    }
}

