class Solution {
    /**
     * Checks if the given string is a palindrome.
     *
     * @param text The string to check.
     * @return true if the string is a palindrome, false otherwise.
     */
    public boolean is_palindrome(String text) {
        int left = 0;
        int right = text.length() - 1;

        while (left < right) {
            if (text.charAt(left) != text.charAt(right)) {
                return false;
            }
            left++;
            right--;
        }

        return true;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.is_palindrome(""));      // true
        System.out.println(solution.is_palindrome("aba"));   // true
        System.out.println(solution.is_palindrome("aaaaa")); // true
        System.out.println(solution.is_palindrome("zbcd"));  // false
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    /**
    Return 2^n modulo p (be aware of numerics).
    >>> modp(3, 5)
    3
    >>> modp(1101, 101)
    2
    >>> modp(0, 101)
    1
    >>> modp(3, 11)
    8
    >>> modp(100, 101)
    1
     */
    public int modp(int n, int p) {
        // Base case: 2^0 % p = 1
        if (n == 0) {
            return 1;
        }

        // Initialize result
        int result = 1;

        // Convert base to 2
        int base = 2;

        // Loop until all bits of n are processed
        while (n > 0) {
            // If the current bit of n is 1, multiply the result by the base
            if ((n & 1) == 1) {
                result = (result * base) % p;
            }

            // Square the base and reduce it modulo p
            base = (base * base) % p;

            // Right shift n by 1
            n >>= 1;
        }

        return result;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.modp(3, 5));    // Output: 3
        System.out.println(sol.modp(1101, 101)); // Output: 2
        System.out.println(sol.modp(0, 101));   // Output: 1
        System.out.println(sol.modp(3, 11));    // Output: 8
        System.out.println(sol.modp(100, 101)); // Output: 1
    }
}

