class Solution {
    /**
    Return n-th Fibonacci number.
    >>> fib(10)
    55
    >>> fib(1)
    1
    >>> fib(8)
    21
     */
    public int fib(int n) {
        if (n <= 1) {
            return n;
        }

        int a = 0, b = 1, c;
        for (int i = 2; i <= n; i++) {
            c = a + b;
            a = b;
            b = c;
        }
        return b;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.fib(10)); // Output: 55
        System.out.println(solution.fib(1));  // Output: 1
        System.out.println(solution.fib(8));  // Output: 21
    }
}

import java.util.*;

class Solution {
    /**
    brackets is a string of "<" and ">".
    return True if every opening bracket has a corresponding closing bracket.

    >>> correct_bracketing("<")
    false
    >>> correct_bracketing("<>")
    true
    >>> correct_bracketing("<<><>>")
    true
    >>> correct_bracketing("><<>")
    false
     */
    public boolean correct_bracketing(String brackets) {
        Stack<Character> stack = new Stack<>();

        for (char ch : brackets.toCharArray()) {
            if (ch == '<') {
                stack.push(ch);
            } else if (ch == '>') {
                if (stack.isEmpty()) {
                    return false; // No matching opening bracket
                }
                stack.pop();
            }
        }

        return stack.isEmpty(); // All opening brackets must be matched
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.correct_bracketing("<")); // false
        System.out.println(solution.correct_bracketing("<>")); // true
        System.out.println(solution.correct_bracketing("<<><>>")); // true
        System.out.println(solution.correct_bracketing("><<>")); // false
    }
}

