class Solution {
    /**
     * Returns true if the list elements are monotonically increasing or decreasing.
     * 
     * @param l The list of integers to check.
     * @return true if the list is monotonically increasing or decreasing, false otherwise.
     */
    public boolean monotonic(List<Integer> l) {
        if (l == null || l.size() <= 1) {
            return true; // A list with 0 or 1 element is trivially monotonic.
        }

        boolean increasing = true;
        boolean decreasing = true;

        for (int i = 1; i < l.size(); i++) {
            if (l.get(i) > l.get(i - 1)) {
                decreasing = false;
            }
            if (l.get(i) < l.get(i - 1)) {
                increasing = false;
            }
        }

        return increasing || decreasing;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.monotonic(Arrays.asList(1, 2, 4, 20))); // true
        System.out.println(solution.monotonic(Arrays.asList(1, 20, 4, 10))); // false
        System.out.println(solution.monotonic(Arrays.asList(4, 1, 0, -10))); // true
    }
}

