class Solution {
    /**
    Return sorted unique common elements for two lists.
    >>> common(Arrays.asList(1, 4, 3, 34, 653, 2, 5), Arrays.asList(5, 7, 1, 5, 9, 653, 121))
    [1, 5, 653]
    >>> common(Arrays.asList(5, 3, 2, 8), Arrays.asList(3, 2))
    [2, 3]
     */
    public List<Integer> common(List<Integer> l1, List<Integer> l2) {
        // Step 1: Create a set from the first list to ensure uniqueness and efficient lookup
        Set<Integer> set1 = new HashSet<>(l1);

        // Step 2: Create a set to store common elements
        Set<Integer> commonElements = new HashSet<>();

        // Step 3: Iterate through the second list and add common elements to the set
        for (Integer num : l2) {
            if (set1.contains(num)) {
                commonElements.add(num);
            }
        }

        // Step 4: Convert the set of common elements to a list and sort it
        List<Integer> result = new ArrayList<>(commonElements);
        Collections.sort(result);

        // Step 5: Return the sorted list
        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.common(Arrays.asList(1, 4, 3, 34, 653, 2, 5), Arrays.asList(5, 7, 1, 5, 9, 653, 121))); // [1, 5, 653]
        System.out.println(solution.common(Arrays.asList(5, 3, 2, 8), Arrays.asList(3, 2))); // [2, 3]
    }
}

