class Solution {
    /**
     * Return the largest prime factor of n. Assume n > 1 and is not a prime.
     * 
     * @param n The number to find the largest prime factor of.
     * @return The largest prime factor of n.
     */
    public int largest_prime_factor(int n) {
        int maxPrime = -1;

        // Divide n by 2 to remove all even factors
        while (n % 2 == 0) {
            maxPrime = 2;
            n /= 2;
        }

        // n must be odd at this point, so we can skip one element (Note i = i + 2)
        for (int i = 3; i <= Math.sqrt(n); i += 2) {
            // While i divides n, print i and divide n
            while (n % i == 0) {
                maxPrime = i;
                n = n / i;
            }
        }

        // This condition is to handle the case when n is a prime number
        // greater than 2
        if (n > 2) {
            maxPrime = n;
        }

        return maxPrime;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.largest_prime_factor(13195)); // Output: 29
        System.out.println(solution.largest_prime_factor(2048));  // Output: 2
    }
}

