class Solution {
    public List<Integer> derivative(List<Integer> xs) {
        // Create a new list to store the derivative coefficients
        List<Integer> derivative = new ArrayList<>();

        // Iterate through the coefficients starting from the first non-constant term
        for (int i = 1; i < xs.size(); i++) {
