import java.util.*;

class Solution {
    public int fibfib(int n) {
        if (n < 2) return 0;
        if (n == 2) return 1;

        // Initialize an array to store the computed values
        int[] dp = new int[n + 1];
        dp[0] = 0;
        dp[1] = 0;
        dp[2] = 1;

        // Compute the values from 3 to n
        for (int i = 3; i <= n; i++) {
            dp[i] = dp[i - 1] + dp[i - 2] + dp[i - 3];
        }

        return dp[n];
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.fibfib(1)); // Output: 0
        System.out.println(solution.fibfib(5)); // Output: 4
        System.out.println(solution.fibfib(8)); // Output: 24
    }
}

