import java.util.*;

class Solution {
    /**
    Circular shift the digits of the integer x, shift the digits right by shift
    and return the result as a string.
    If shift > number of digits, return digits reversed.
    >>> circular_shift(12, 1)
    "21"
    >>> circular_shift(12, 2)
    "12"
     */
    public String circular_shift(int x, int shift) {
        // Convert the integer to a string to easily manipulate its digits
        String numStr = Integer.toString(x);
        int len = numStr.length();

        // If shift is greater than the length of the number, reverse the digits
        if (shift >= len) {
            return new StringBuilder(numStr).reverse().toString();
        }

        // Calculate the effective shift (in case shift is larger than the length)
        shift = shift % len;

        // Perform the circular shift
        String shifted = numStr.substring(len - shift) + numStr.substring(0, len - shift);

        return shifted;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.circular_shift(12, 1)); // Output: "21"
        System.out.println(sol.circular_shift(12, 2)); // Output: "12"
        System.out.println(sol.circular_shift(12345, 3)); // Output: "34512"
        System.out.println(sol.circular_shift(12345, 7)); // Output: "54321"
    }
}

