import java.util.regex.Matcher;
import java.util.regex.Pattern;

class Solution {
    public int fruit_distribution(String s, int n) {
        // Use regular expressions to find the numbers of apples and oranges
        Pattern pattern = Pattern.compile("(\\d+) apples and (\\d+) oranges");
        Matcher matcher = pattern.matcher(s);

        if (matcher.find()) {
            // Convert the found strings to integers
            int apples = Integer.parseInt(matcher.group(1));
            int oranges = Integer.parseInt(matcher.group(2));

            // Calculate the number of mangoes
            int mangoes = n - (apples + oranges);
            return mangoes;
        } else {
            // If the pattern is not found, throw an exception or handle it as needed
            throw new IllegalArgumentException("Invalid input format");
        }
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.fruit_distribution("5 apples and 6 oranges", 19)); // Output: 8
        System.out.println(solution.fruit_distribution("0 apples and 1 oranges", 3));  // Output: 2
        System.out.println(solution.fruit_distribution("2 apples and 3 oranges", 100)); // Output: 95
        System.out.println(solution.fruit_distribution("100 apples and 1 oranges", 120)); // Output: 19
    }
}

