class Solution {
    public List<Integer> pluck(List<Integer> arr) {
        // Initialize variables to store the smallest even value and its index
        int smallestEvenValue = Integer.MAX_VALUE;
        int smallestIndex = -1;

        // Iterate through the list to find the smallest even value and its index
        for (int i = 0; i < arr.size(); i++) {
            int value = arr.get(i);
            if (value % 2 == 0 && value < smallestEvenValue) {
                smallestEvenValue = value;
                smallestIndex = i;
            }
        }

        // If no even value was found, return an empty list
        if (smallestIndex == -1) {
            return new ArrayList<>();
        }

        // Return the result as a list containing the smallest even value and its index
        return Arrays.asList(smallestEvenValue, smallestIndex);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.pluck(Arrays.asList(4, 2, 3))); // Output: [2, 1]
        System.out.println(solution.pluck(Arrays.asList(1, 2, 3))); // Output: [2, 1]
        System.out.println(solution.pluck(new ArrayList<>()));      // Output: []
        System.out.println(solution.pluck(Arrays.asList(5, 0, 3, 0, 4, 2))); // Output: [0, 1]
    }
}

