class Solution {
    public int search(List<Integer> lst) {
        // Step 1: Count the frequency of each integer
        Map<Integer, Integer> frequencyMap = new HashMap<>();
        for (int num : lst) {
            frequencyMap.put(num, frequencyMap.getOrDefault(num, 0) + 1);
        }

        // Step 2: Find the maximum integer that satisfies the condition
        int result = -1;
        for (Map.Entry<Integer, Integer> entry : frequencyMap.entrySet()) {
            int num = entry.getKey();
            int freq = entry.getValue();
            if (num <= freq && num > result) {
                result = num;
            }
        }

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.search(Arrays.asList(4, 1, 2, 2, 3, 1))); // Output: 2
        System.out.println(solution.search(Arrays.asList(1, 2, 2, 3, 3, 3, 4, 4, 4))); // Output: 3
        System.out.println(solution.search(Arrays.asList(5, 5, 4, 4, 4))); // Output: -1
    }
}

import java.util.*;

class Solution {
    public List<Integer> strange_sort_list(List<Integer> lst) {
        // Sort the list in ascending order
        Collections.sort(lst);

        List<Integer> result = new ArrayList<>();

        while (!lst.isEmpty()) {
            // Add the minimum value (first element)
            result.add(lst.remove(0));

            // If the list is not empty, add the maximum value (last element)
            if (!lst.isEmpty()) {
                result.add(lst.remove(lst.size() - 1));
            }
        }

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.strange_sort_list(Arrays.asList(1, 2, 3, 4))); // [1, 4, 2, 3]
        System.out.println(solution.strange_sort_list(Arrays.asList(5, 5, 5, 5))); // [5, 5, 5, 5]
        System.out.println(solution.strange_sort_list(Arrays.asList()));           // []
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given the lengths of the three sides of a triangle. Return the area of
    the triangle rounded to 2 decimal points if the three sides form a valid triangle.
    Otherwise return -1
    Three sides make a valid triangle when the sum of any two sides is greater
    than the third side.
    Example:
    triangle_area(3, 4, 5) == 6.00
    triangle_area(1, 2, 10) == -1
     */
    public double triangle_area(double a, double b, double c) {
        // Check if the sides form a valid triangle
        if (a + b > c && a + c > b && b + c > a) {
            // Calculate the semi-perimeter
            double s = (a + b + c) / 2;
            // Calculate the area using Heron's formula
            double area = Math.sqrt(s * (s - a) * (s - b) * (s - c));
            // Round the area to 2 decimal places
            return Math.round(area * 100.0) / 100.0;
        } else {
            // Return -1 if the sides do not form a valid triangle
            return -1;
        }
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.triangle_area(3, 4, 5)); // Expected output: 6.00
        System.out.println(solution.triangle_area(1, 2, 10)); // Expected output: -1
    }
}

import java.util.*;

class Solution {
    public boolean will_it_fly(List<Integer> q, int w) {
        // Check if the list is a palindrome
        boolean isBalanced = isPalindrome(q);

        // Calculate the sum of the elements in the list
        int sum = q.stream().mapToInt(Integer::intValue).sum();

        // Check if the list is balanced and the sum is within the weight limit
        return isBalanced && sum <= w;
    }

    private boolean isPalindrome(List<Integer> q) {
        int left = 0;
        int right = q.size() - 1;

        while (left < right) {
            if (!q.get(left).equals(q.get(right))) {
                return false;
            }
            left++;
            right--;
        }

        return true;
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public int smallest_change(List<Integer> arr) {
        int left = 0;
        int right = arr.size() - 1;
        int changes = 0;

        while (left < right) {
            if (!arr.get(left).equals(arr.get(right))) {
                changes++;
            }
            left++;
            right--;
        }

        return changes;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.smallest_change(Arrays.asList(1, 2, 3, 5, 4, 7, 9, 6))); // Output: 4
        System.out.println(solution.smallest_change(Arrays.asList(1, 2, 3, 4, 3, 2, 2))); // Output: 1
        System.out.println(solution.smallest_change(Arrays.asList(1, 2, 3, 2, 1))); // Output: 0
    }
}

import java.util.*;

class Solution {
    public List<String> total_match(List<String> lst1, List<String> lst2) {
        // Calculate the total number of characters in lst1
        int totalChars1 = lst1.stream().mapToInt(String::length).sum();

        // Calculate the total number of characters in lst2
        int totalChars2 = lst2.stream().mapToInt(String::length).sum();

        // Compare the total number of characters and return the appropriate list
        if (totalChars1 <= totalChars2) {
            return lst1;
        } else {
            return lst2;
        }
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.total_match(Arrays.asList(), Arrays.asList())); // []
        System.out.println(solution.total_match(Arrays.asList("hi", "admin"), Arrays.asList("hI", "Hi"))); // ["hI", "Hi"]
        System.out.println(solution.total_match(Arrays.asList("hi", "admin"), Arrays.asList("hi", "hi", "admin", "project"))); // ["hi", "admin"]
        System.out.println(solution.total_match(Arrays.asList("hi", "admin"), Arrays.asList("hI", "hi", "hi"))); // ["hI", "hi", "hi"]
        System.out.println(solution.total_match(Arrays.asList("4"), Arrays.asList("1", "2", "3", "4", "5"))); // ["4"]
    }
}

import java.util.*;

class Solution {
    // Helper method to generate a list of prime numbers less than 100
    private List<Integer> generatePrimes() {
        List<Integer> primes = new ArrayList<>();
        boolean[] isPrime = new boolean[100];
        Arrays.fill(isPrime, true);
        isPrime[0] = isPrime[1] = false; // 0 and 1 are not prime numbers

        for (int i = 2; i < 100; i++) {
            if (isPrime[i]) {
                primes.add(i);
                for (int j = i * i; j < 100; j += i) {
                    isPrime[j] = false;
                }
            }
        }
        return primes;
    }

    public boolean is_multiply_prime(int a) {
        List<Integer> primes = generatePrimes();

        // Try to find three primes whose product equals 'a'
        for (int i = 0; i < primes.size(); i++) {
            for (int j = i; j < primes.size(); j++) {
                for (int k = j; k < primes.size(); k++) {
                    if (primes.get(i) * primes.get(j) * primes.get(k) == a) {
                        return true;
                    }
                }
            }
        }
        return false;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.is_multiply_prime(30)); // true
        System.out.println(solution.is_multiply_prime(105)); // true
        System.out.println(solution.is_multiply_prime(44)); // false
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public boolean is_simple_power(int x, int n) {
        // Edge case: if n is 1, x must also be 1 to be a simple power
        if (n == 1) {
            return x == 1;
        }

        // Edge case: if x is 0, it cannot be a simple power of any n except 0
        if (x == 0) {
            return n == 0;
        }

        // Loop to check if x is a simple power of n
        while (x % n == 0) {
            x /= n;
        }

        // If x is reduced to 1, it is a simple power of n
        return x == 1;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.is_simple_power(1, 4)); // true
        System.out.println(solution.is_simple_power(2, 2)); // true
        System.out.println(solution.is_simple_power(8, 2)); // true
        System.out.println(solution.is_simple_power(3, 2)); // false
        System.out.println(solution.is_simple_power(3, 1)); // false
        System.out.println(solution.is_simple_power(5, 3)); // false
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    /**
    Write a function that takes an integer a and returns true
    if this integer is a cube of some integer number.
    Note: you may assume the input is always valid.
    Examples:
    iscube(1) ==> true
    iscube(2) ==> false
    iscube(-1) ==> true
    iscube(64) ==> true
    iscube(0) ==> true
    iscube(180) ==> false
     */
    public boolean iscube(int a) {
        // Calculate the cube root of the absolute value of a
        double cubeRoot = Math.cbrt(Math.abs(a));

        // Round the cube root to the nearest integer
        int roundedCubeRoot = (int) Math.round(cubeRoot);

        // Check if the cube of the rounded cube root equals the original number
        return Math.pow(roundedCubeRoot, 3) == Math.abs(a);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.iscube(1));    // true
        System.out.println(solution.iscube(2));    // false
        System.out.println(solution.iscube(-1));   // true
        System.out.println(solution.iscube(64));   // true
        System.out.println(solution.iscube(0));    // true
        System.out.println(solution.iscube(180));  // false
    }
}

import java.util.*;

class Solution {
    public int hex_key(String num) {
        // Set of prime hexadecimal digits
        Set<Character> primeHexDigits = new HashSet<>(Arrays.asList('2', '3', '5', '7', 'B', 'D'));

        // Counter for prime digits
        int count = 0;

        // Iterate through each character in the input string
        for (char c : num.toCharArray()) {
            // Check if the character is a prime hexadecimal digit
            if (primeHexDigits.contains(c)) {
                count++;
            }
        }

        return count;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.hex_key("AB")); // Output: 1
        System.out.println(solution.hex_key("1077E")); // Output: 2
        System.out.println(solution.hex_key("ABED1A33")); // Output: 4
        System.out.println(solution.hex_key("123456789ABCDEF0")); // Output: 6
        System.out.println(solution.hex_key("2020")); // Output: 2
    }
}

