class Solution {
    /**
    Given a non-empty list of integers lst. Add the even elements that are at odd indices.

    Examples:
        add(Arrays.asList(4, 2, 6, 7)) ==> 2
     */
    public int add(List<Integer> lst) {
        int sum = 0;

        // Iterate through the list, starting from index 1 (the first odd index)
        for (int i = 1; i < lst.size(); i += 2) {
            int element = lst.get(i);
            if (element % 2 == 0) {
                sum += element;
            }
        }

        return sum;
    }

    // Main method for testing
    public static void main(String[] args) {
        Solution solution = new Solution();
        List<Integer> exampleList = Arrays.asList(4, 2, 6, 7);
        System.out.println(solution.add(exampleList)); // Output should be 2
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public String anti_shuffle(String s) {
        // Split the string into words using a regular expression that matches spaces
        String[] words = s.split("(?<=\\s)|(?=\\s)");

        // Process each word to sort its characters
        for (int i = 0; i < words.length; i++) {
            if (!words[i].trim().isEmpty()) { // Check if the word is not just a space
                char[] chars = words[i].toCharArray();
                Arrays.sort(chars);
                words[i] = new String(chars);
            }
        }

        // Join the processed words back into a single string
        return String.join("", words);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.anti_shuffle("Hi")); // Output: "Hi"
        System.out.println(solution.anti_shuffle("hello")); // Output: "ehllo"
        System.out.println(solution.anti_shuffle("Hello World!!!")); // Output: "Hello !!!Wdlor"
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public List<List<Integer>> get_row(List<List<Integer>> lst, int x) {
        List<List<Integer>> result = new ArrayList<>();

        // Iterate through the list to find all occurrences of x
        for (int i = 0; i < lst.size(); i++) {
            List<Integer> row = lst.get(i);
            for (int j = 0; j < row.size(); j++) {
                if (row.get(j) == x) {
                    result.add(Arrays.asList(i, j));
                }
            }
        }

        // Sort the result list
        // First by row in ascending order
        // Then by column in descending order within each row
        result.sort((a, b) -> {
            if (!a.get(0).equals(b.get(0))) {
                return a.get(0) - b.get(0); // Ascending order by row
            } else {
                return b.get(1) - a.get(1); // Descending order by column
            }
        });

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        List<List<Integer>> lst1 = Arrays.asList(
            Arrays.asList(1, 2, 3, 4, 5, 6),
            Arrays.asList(1, 2, 3, 4, 1, 6),
            Arrays.asList(1, 2, 3, 4, 5, 1)
        );
        System.out.println(solution.get_row(lst1, 1)); // Expected: [[0, 0], [1, 4], [1, 0], [2, 5], [2, 0]]

        List<List<Integer>> lst2 = new ArrayList<>();
        System.out.println(solution.get_row(lst2, 1)); // Expected: []

        List<List<Integer>> lst3 = Arrays.asList(
            new ArrayList<>(),
            Arrays.asList(1),
            Arrays.asList(1, 2, 3)
        );
        System.out.println(solution.get_row(lst3, 3)); // Expected: [[2, 2]]
    }
}

import java.util.*;

class Solution {
    public List<Integer> sort_array(List<Integer> array) {
        // Check if the list is empty or has only one element
        if (array == null || array.size() <= 1) {
            return new ArrayList<>(array);
        }

        // Calculate the sum of the first and last elements
        int sum = array.get(0) + array.get(array.size() - 1);

        // Create a copy of the original list
        List<Integer> sortedArray = new ArrayList<>(array);

        // Determine the sorting order and sort the list
        if (sum % 2 == 0) {
            // Sort in descending order if the sum is even
            sortedArray.sort(Comparator.reverseOrder());
        } else {
            // Sort in ascending order if the sum is odd
            sortedArray.sort(Comparator.naturalOrder());
        }

        return sortedArray;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.sort_array(Arrays.asList())); // []
        System.out.println(solution.sort_array(Arrays.asList(5))); // [5]
        System.out.println(solution.sort_array(Arrays.asList(2, 4, 3, 0, 1, 5))); // [0, 1, 2, 3, 4, 5]
        System.out.println(solution.sort_array(Arrays.asList(2, 4, 3, 0, 1, 5, 6))); // [6, 5, 4, 3, 2, 1, 0]
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    /**
     * Encrypts the given string by shifting each letter down by 8 places.
     * 
     * @param s The input string to be encrypted.
     * @return The encrypted string.
     */
    public String encrypt(String s) {
        StringBuilder encrypted = new StringBuilder();

        for (char c : s.toCharArray()) {
            if (Character.isLetter(c)) {
                char base = Character.isUpperCase(c) ? 'A' : 'a';
                // Calculate the new character after shifting by 8 places
                char shiftedChar = (char) ((c - base + 8) % 26 + base);
                encrypted.append(shiftedChar);
            } else {
                // Non-alphabetic characters remain unchanged
                encrypted.append(c);
            }
        }

        return encrypted.toString();
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        System.out.println(solution.encrypt("hi")); // Output: "lm"
        System.out.println(solution.encrypt("asdfghjkl")); // Output: "ewhjklnop"
        System.out.println(solution.encrypt("gf")); // Output: "kj"
        System.out.println(solution.encrypt("et")); // Output: "ix"
        System.out.println(solution.encrypt("Hello, World!")); // Output: "Pmttw, Ycfsk!"
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public Optional<Integer> next_smallest(List<Integer> lst) {
        // Check for edge cases
        if (lst == null || lst.size() < 2) {
            return Optional.empty();
        }

        // Use a TreeSet to automatically sort and remove duplicates
        TreeSet<Integer> sortedSet = new TreeSet<>(lst);

        // If there are less than 2 unique elements, return Optional.empty
        if (sortedSet.size() < 2) {
            return Optional.empty();
        }

        // Get the second smallest element
        Integer secondSmallest = null;
        Iterator<Integer> iterator = sortedSet.iterator();
        iterator.next(); // Skip the smallest element
        if (iterator.hasNext()) {
            secondSmallest = iterator.next();
        }

        return Optional.ofNullable(secondSmallest);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.next_smallest(Arrays.asList(1, 2, 3, 4, 5))); // Optional[2]
        System.out.println(solution.next_smallest(Arrays.asList(5, 1, 4, 3, 2))); // Optional[2]
        System.out.println(solution.next_smallest(Arrays.asList())); // Optional.empty
        System.out.println(solution.next_smallest(Arrays.asList(1, 1))); // Optional.empty
    }
}

import java.util.*;

class Solution {
    public int is_bored(String S) {
        // Split the string into sentences using the delimiters '.', '?', '!'
        String[] sentences = S.split("[.?!]");

        int boredomCount = 0;

        // Iterate through each sentence
        for (String sentence : sentences) {
            // Trim leading and trailing whitespace
            String trimmedSentence = sentence.trim();

            // Check if the sentence starts with "I"
            if (trimmedSentence.startsWith("I ")) {
                boredomCount++;
            }
        }

        return boredomCount;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.is_bored("Hello world")); // Expected output: 0
