class Solution {
    /**
    Create a function that takes 3 numbers.
    Returns true if one of the numbers is equal to the sum of the other two, and all numbers are integers.
    Returns false in any other cases.

    Examples
    any_int(5, 2, 7) -> true

    any_int(3, 2, 2) -> false

    any_int(3, -2, 1) -> true

    any_int(3.6, -2.2, 2) -> false
     */
    public boolean any_int(Object x, Object y, Object z) {
        // Check if all inputs are instances of Integer
        if (!(x instanceof Integer) || !(y instanceof Integer) || !(z instanceof Integer)) {
            return false;
        }

        // Cast the objects to Integer
        int a = (Integer) x;
        int b = (Integer) y;
        int c = (Integer) z;

        // Check if any number is equal to the sum of the other two
        return (a == b + c) || (b == a + c) || (c == a + b);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.any_int(5, 2, 7));  // true
        System.out.println(solution.any_int(3, 2, 2));  // false
        System.out.println(solution.any_int(3, -2, 1)); // true
        System.out.println(solution.any_int(3.6, -2.2, 2)); // false
    }
}

import java.util.*;

class Solution {
    public String encode(String message) {
        // Define a map for vowel replacements
        Map<Character, Character> vowelMap = new HashMap<>();
        vowelMap.put('a', 'c');
        vowelMap.put('e', 'g');
        vowelMap.put('i', 'k');
        vowelMap.put('o', 'q');
        vowelMap.put('u', 'w');
        vowelMap.put('A', 'C');
        vowelMap.put('E', 'G');
        vowelMap.put('I', 'K');
        vowelMap.put('O', 'Q');
        vowelMap.put('U', 'W');

        StringBuilder encodedMessage = new StringBuilder();

        for (char ch : message.toCharArray()) {
            // Swap case
            if (Character.isUpperCase(ch)) {
                ch = Character.toLowerCase(ch);
            } else if (Character.isLowerCase(ch)) {
                ch = Character.toUpperCase(ch);
            }

            // Replace vowels
            if (vowelMap.containsKey(ch)) {
                ch = vowelMap.get(ch);
            }

            encodedMessage.append(ch);
        }

        return encodedMessage.toString();
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.encode("test")); // Output: TGST
        System.out.println(solution.encode("This is a message")); // Output: tHKS KS C MGSSCGG
    }
}

