class Solution {
    // Helper function to check if a number is prime
    private boolean isPrime(int num) {
        if (num <= 1) return false;
        if (num == 2) return true;
        if (num % 2 == 0) return false;
        for (int i = 3; i * i <= num; i += 2) {
            if (num % i == 0) return false;
        }
        return true;
    }

    // Function to sum the digits of a number
    private int sumOfDigits(int num) {
        int sum = 0;
        while (num > 0) {
            sum += num % 10;
            num /= 10;
        }
        return sum;
    }

    public int skjkasdkd(List<Integer> lst) {
        int largestPrime = -1;

        // Find the largest prime number in the list
        for (int num : lst) {
            if (isPrime(num) && num > largestPrime) {
                largestPrime = num;
            }
        }

        // If no prime number is found, return 0
        if (largestPrime == -1) return 0;

        // Return the sum of the digits of the largest prime number
        return sumOfDigits(largestPrime);
    }

