import java.util.*;

class Solution {
    public boolean check_dict_case(Map<Object, Object> dict) {
        // Check if the map is empty
        if (dict.isEmpty()) {
            return false;
        }

        boolean allLowerCase = true;
        boolean allUpperCase = true;

        for (Object key : dict.keySet()) {
            // Check if the key is not a string
            if (!(key instanceof String)) {
                return false;
            }

            String strKey = (String) key;

            // Check if the key is not all lowercase
            if (!strKey.equals(strKey.toLowerCase())) {
                allLowerCase = false;
            }

            // Check if the key is not all uppercase
            if (!strKey.equals(strKey.toUpperCase())) {
                allUpperCase = false;
            }
        }

        // Return true if all keys are either all lowercase or all uppercase
        return allLowerCase || allUpperCase;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        System.out.println(solution.check_dict_case(Map.of("a", "apple", "b", "banana"))); // True
        System.out.println(solution.check_dict_case(Map.of("a", "apple", "A", "banana", "B", "banana"))); // False
        System.out.println(solution.check_dict_case(Map.of("a", "apple", 8, "banana", "a", "apple"))); // False
        System.out.println(solution.check_dict_case(Map.of("Name", "John", "Age", "36", "City", "Houston"))); // False
        System.out.println(solution.check_dict_case(Map.of("STATE", "NC", "ZIP", "12345"))); // True
    }
}

import java.util.*;

class Solution {
    /**
     * Returns a list of prime numbers less than n.
     *
     * @param n The upper limit (non-inclusive).
     * @return A list of prime numbers less than n.
     */
    public List<Integer> count_up_to(int n) {
        List<Integer> primes = new ArrayList<>();

        for (int i = 2; i < n; i++) {
            if (isPrime(i)) {
                primes.add(i);
            }
        }

        return primes;
    }

    /**
     * Checks if a number is prime.
     *
     * @param num The number to check.
     * @return true if the number is prime, false otherwise.
     */
    private boolean isPrime(int num) {
        if (num <= 1) {
            return false;
        }
        if (num == 2) {
            return true;
        }
        if (num % 2 == 0) {
            return false;
        }
        for (int i = 3; i <= Math.sqrt(num); i += 2) {
            if (num % i == 0) {
                return false;
            }
        }
        return true;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.count_up_to(5));   // [2, 3]
        System.out.println(solution.count_up_to(11));  // [2, 3, 5, 7]
        System.out.println(solution.count_up_to(0));   // []
        System.out.println(solution.count_up_to(20));  // [2, 3, 5, 7, 11, 13, 17, 19]
        System.out.println(solution.count_up_to(1));   // []
        System.out.println(solution.count_up_to(18));  // [2, 3, 5, 7, 11, 13, 17]
    }
}

