class Solution {
    public int closest_integer(String value) {
        // Parse the string to a double
        double num = Double.parseDouble(value);

        // Determine the floor and ceiling values
        double floorValue = Math.floor(num);
        double ceilValue = Math.ceil(num);

        // Calculate the distance from the floor and ceiling values
        double distFromFloor = num - floorValue;
        double distFromCeil = ceilValue - num;

        // Check if the number is exactly halfway between two integers
        if (distFromFloor == 0.5) {
            // Round away from zero
            return num > 0 ? (int) ceilValue : (int) floorValue;
        } else {
            // Use the built-in round function which rounds to the nearest integer
            return (int) Math.round(num);
        }
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.closest_integer("10"));    // Output: 10
        System.out.println(sol.closest_integer("15.3"));  // Output: 15
        System.out.println(sol.closest_integer("14.5"));  // Output: 15
        System.out.println(sol.closest_integer("-14.5")); // Output: -15
    }
}

import java.util.*;

class Solution {
    public List<Integer> make_a_pile(int n) {
        List<Integer> pile = new ArrayList<>();

        // The first level has n stones
        int currentStones = n;

        for (int i = 0; i < n; i++) {
            pile.add(currentStones);

            // Determine the next number of stones
            if (currentStones % 2 == 0) {
                // If currentStones is even, the next number is the next even number
                currentStones += 2;
            } else {
                // If currentStones is odd, the next number is the next odd number
                currentStones += 2;
            }
        }

        return pile;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.make_a_pile(3)); // Output: [3, 5, 7]
    }
}

